mapper.update:
UPDATE interview_position
SET group_id = NULL
WHERE DATE (start_time) = #{startDate}

mapper.select:
SELECT COUNT(*)
FROM interview_position
WHERE location = #{location}
 AND start_time = #{startTime}

mapper.update:
UPDATE interview_position
    SET group_id = #{groupId}
WHERE location = #{location}
  AND start_time = #{startTime}
